const DICTIONARY_WORDS = Object.values(DICTIONARY_BY_LETTER).flat();
const VALID_GUESSES = [...new Set([...ANSWER_WORDS, ...EXTRA_GUESSES, ...DICTIONARY_WORDS])];

function isValidGuess(word) {
  return VALID_GUESSES.includes(word.toUpperCase());
}

function getDailyIndex(date = new Date()) {
  const utcDate = Date.UTC(date.getUTCFullYear(), date.getUTCMonth(), date.getUTCDate());
  return Math.floor(utcDate / 86_400_000) % ANSWER_WORDS.length;
}

function getRandomAnswer(seedDate = new Date()) {
  return ANSWER_WORDS[getDailyIndex(seedDate)];
}

export { ANSWER_WORDS, EXTRA_GUESSES, DICTIONARY_BY_LETTER, VALID_GUESSES, isValidGuess, getDailyIndex, getRandomAnswer };
